SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION apap_obter_texto_superior (nr_seq_linha_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_formato_w	        varchar(255);
ds_label_w		        varchar(255);
ds_formato_composto		varchar(255);

c_formato CURSOR FOR
	SELECT	upper(a.ds_texto_antes||
			'@'||a.nm_atributo||CASE WHEN c.ie_componente='lcb' THEN '.ds'  ELSE null END ||'#'||
			a.ds_texto_depois) ds_formato,
			a.ds_texto_antes||
			' '||obter_desc_expressao(b.cd_exp_informacao)||' '||
			a.ds_texto_depois ds_label,
			a.nr_ordem_composto
	from	documento_subsecao a,
			linked_data_atributo b,
			tabela_visao_atributo c
	where	a.nr_seq_linked_data = b.nr_seq_linked_data
	and		a.nm_atributo = b.nm_atributo_linked
	and		b.nm_atributo = c.nm_atributo
	and		b.nr_seq_visao = c.nr_sequencia
	and		coalesce(a.ie_mostrar_linha_sup,'N')	= 'S'
	and		a.nr_seq_superior 				= nr_seq_linha_p
	
union

	SELECT	upper(a.ds_texto_antes||'@'||a.nm_atributo||'#'||a.ds_texto_depois) ds_formato,
			a.ds_texto_antes||' '||b.ds_label||' '||a.ds_texto_depois ds_label,
			a.nr_ordem_composto
	from	documento_subsecao a,
			ehr_template_conteudo b
	where	a.nr_seq_temp_conteudo = b.nr_sequencia
	and		coalesce(a.ie_mostrar_linha_sup,'N')	= 'S'
	and		a.nr_seq_superior 				= nr_seq_linha_p
	order by nr_ordem_composto;

BEGIN
if (nr_seq_linha_p IS NOT NULL AND nr_seq_linha_p::text <> '') then
	<<read_registros>>
	for r_formato in c_formato
		loop
		ds_formato_w 	:= ds_formato_w ||r_formato.ds_formato;
		ds_label_w		:= ds_label_w ||r_formato.ds_label;
		end loop read_registros;
end if;	

if (ie_opcao_p = 'L') then
	ds_formato_composto := substr(ds_label_w,1,255);
elsif (ie_opcao_p = 'F') then
	ds_formato_composto := substr(ds_formato_w,1,255);
end if;	
return ds_formato_composto;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION apap_obter_texto_superior (nr_seq_linha_p bigint, ie_opcao_p text) FROM PUBLIC;

