CREATE OR REPLACE FUNCTION aprovar_gap_etapa ( nr_seq_processo_p bigint, nr_seq_etapa_p bigint, dt_prevista_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_etapa_w	bigint := 0;
ie_aprova_w	varchar(2) := 'N';
ie_resultado_w	varchar(1):= 'X';

c01 CURSOR FOR
	SELECT	ie_resultado
	from	gap_processo_etapa
	where	nr_seq_processo = nr_seq_processo_p
	and	dt_prevista < dt_prevista_p
	order by	dt_prevista;


BEGIN

select	count(*)
into STRICT	qt_etapa_w
from	gap_processo_etapa
where	nr_seq_processo = nr_seq_processo_p;

if (qt_etapa_w = 0) then
	ie_aprova_w	:= 'N';
elsif (qt_etapa_w = 1) then
	ie_aprova_w	:= 'S';
else	begin
	open c01;
		loop
		fetch c01 into
			ie_resultado_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
		end loop;
	close c01;

	if (ie_resultado_w = 'A') then
		ie_aprova_w	:= 'S';
	elsif (ie_resultado_w = 'R') then
		ie_aprova_w	:= 'S';
	elsif (ie_resultado_w = 'X') then
		ie_aprova_w	:= 'S';
	elsif (ie_resultado_w = 'N') then
		ie_aprova_w	:= 'N';
	end if;
	end;
end if;

return ie_aprova_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
