SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION arredondondar_grace_ii (nr_valor_p bigint, nr_calculo bigint) RETURNS bigint AS $body$
DECLARE

	nr_resultado_w	bigint;
	nr_multiplicador_w bigint;

	
BEGIN
		if (nr_valor_p < 10) then
			nr_multiplicador_w := power(10, nr_calculo);
			nr_resultado_w := trunc(round(nr_valor_p * nr_multiplicador_w) / nr_multiplicador_w, nr_calculo);

		else
			nr_resultado_w := round(nr_valor_p);

		end if;

	return nr_resultado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
