CREATE OR REPLACE FUNCTION atepacbm_find_location_rule ( nr_seq_topografia_w bigint, atend_lado_w text ) RETURNS bigint AS $body$
DECLARE


    nr_sequencia_retorno_w          sm_location.nr_sequencia%TYPE;
    nr_sequencia_regra_com_side_w   sm_location.nr_sequencia%TYPE;
    nr_sequencia_regra_null_w       sm_location.nr_sequencia%TYPE;


BEGIN
    SELECT
        MIN(nr_sequencia)
    INTO STRICT nr_sequencia_regra_com_side_w
    FROM
        sm_location
    WHERE
        nr_seq_topografia = nr_seq_topografia_w
        AND ie_side = atend_lado_w;

    SELECT
        MIN(nr_sequencia)
    INTO STRICT nr_sequencia_regra_null_w
    FROM
        sm_location
    WHERE
        nr_seq_topografia = nr_seq_topografia_w
        AND coalesce(ie_side::text, '') = '';

    SELECT
        CASE
            WHEN (nr_sequencia_regra_com_side_w IS NOT NULL AND nr_sequencia_regra_com_side_w::text <> '') THEN
                nr_sequencia_regra_com_side_w
            ELSE
                nr_sequencia_regra_null_w
        END
    INTO STRICT nr_sequencia_retorno_w
;

    RETURN nr_sequencia_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
