CREATE OR REPLACE FUNCTION atualizar_centro_custo_ordem ( nr_sequencia_p bigint, cd_pessoa_solicitante_p bigint, cd_centro_custo_p bigint) RETURNS bigint AS $body$
DECLARE

	ie_centro_custo_w  	varchar(1) := '';
	cd_centro_custo_w		integer := cd_centro_custo_p;

BEGIN
	ie_centro_custo_w := obter_parametro_usuario_js(299, 72);
	if (ie_centro_custo_w <> '') then
		if (ie_centro_custo_w = 'E') then
			select 	coalesce(max(a.cd_centro_custo),0)
			into STRICT	cd_centro_custo_w
			from 	man_ordem_servico b,
				man_equipamento a
			where 	b.nr_seq_equipamento = a.nr_sequencia
			and 	b.nr_sequencia = nr_sequencia_p;
		elsif (ie_centro_custo_w = 'L') then
			select 	coalesce(max(c.cd_centro_custo),0)
			into STRICT	cd_centro_custo_w
			from 	man_ordem_servico b,
				man_localizacao a,
				setor_atendimento c
			where 	b.nr_seq_localizacao = a.nr_sequencia
			and 	c.cd_setor_atendimento = a.cd_setor
			and 	b.nr_sequencia = nr_sequencia_p;
		elsif (ie_centro_custo_w = 'S') then
			select 	coalesce(max(b.cd_centro_custo),0)
			into STRICT	cd_centro_custo_w
			from	setor_atendimento b,
				usuario a
			where	b.cd_setor_atendimento = a.cd_setor_atendimento
			and    	a.cd_pessoa_fisica = cd_pessoa_solicitante_p;
		end if;
	end if;
return	cd_centro_custo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
