CREATE OR REPLACE FUNCTION aus_person_get_address ( cd_pessoa_fisica_p text, ie_tipo_compl_p bigint) RETURNS varchar AS $body$
DECLARE


    ds_address_w    varchar(255);

BEGIN
    select  substr(replace(obter_compl_pf(cd_pessoa_fisica_p,ie_tipo_compl_p,'ESC'),' - ',', ')||', '||chr(10)||
            obter_compl_pf(cd_pessoa_fisica_p,ie_tipo_compl_p,'DSM') || CASE WHEN coalesce(obter_compl_pf(cd_pessoa_fisica_p,ie_tipo_compl_p,'DSM'),'0')='0' THEN ''  ELSE ', ' END  ||
            obter_compl_pf(cd_pessoa_fisica_p,ie_tipo_compl_p,'UF')  || CASE WHEN coalesce(obter_compl_pf(cd_pessoa_fisica_p,ie_tipo_compl_p,'UF'),'0')='0' THEN ''  ELSE ', ' END  ||
            obter_compl_pf(cd_pessoa_fisica_p,ie_tipo_compl_p,'CEP'),1,255) ds_address
    into STRICT    ds_address_w
;

    
    return ds_address_w;
    
    
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
