SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION baixa_titulo_receber_js (nr_titulo_p bigint, vl_baixa_p bigint, vl_juros_p bigint, vl_multa_p bigint, cd_estabelecimento_p bigint, /*parametros que não são passados no delphi 
		dt_recebimento_p	date, 
		vl_glosa_p		number, 
		nr_bordero_p		number, 
		vl_rec_maior_p		number, 
		nr_seq_conta_banco_p	number, 
		*/
 nm_usuario_p text) RETURNS bigint AS $body$
DECLARE

 
cd_tipo_receb_w 	integer;
nr_seq_trans_fin_w	bigint;
qt_registros_w		bigint;
ds_retorno_w		bigint;

BEGIN
 
select	max(a.cd_tipo_recebimento) cd_tipo_recebimento, 
	max(a.nr_seq_trans_fin) nr_seq_trans_fin, 
	count(*) qt_registro 
into STRICT	cd_tipo_receb_w, 
	nr_seq_trans_fin_w, 
	qt_registros_w 
from	tipo_recebimento a 
where	a.ie_tipo_consistencia = 13 
and	a.ie_situacao      = 'A';
 
if (coalesce(qt_registros_w::text, '') = '') or (coalesce(cd_tipo_receb_w::text, '') = '') then 
	begin	 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(104866);
	--obter_texto_tasy(104866, wheb_usuario_pck.get_nr_seq_idioma) || '#@#@'); 
	end;
end if;
 
if (coalesce(nr_seq_trans_fin_w::text, '') = '') then 
	begin 
	select	max(a.nr_sequencia) 
	into STRICT	nr_seq_trans_fin_w 
	from	transacao_financeira a 
	where	coalesce(a.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p 
	and	a.ie_situacao = 'A' 
	and	a.ie_acao   = 24;
	end;
end if;
 
--parâmetros comentados não passados no código delphi 
if (vl_juros_p IS NOT NULL AND vl_juros_p::text <> '') or (vl_multa_p IS NOT NULL AND vl_multa_p::text <> '') then 
	begin 
	CALL baixar_tit_rec_juros_multa(cd_estabelecimento_p, 
			cd_tipo_receb_w, 
			nr_titulo_p, 
			nr_seq_trans_fin_w, 
			vl_baixa_p, 
			null,--dt_recebimento_p, 
			nm_usuario_p, 
			null,--nr_seq_conta_banco_p, 
			vl_juros_p, 
			vl_multa_p);
	end;
else 
	begin 
	CALL baixa_titulo_receber(cd_estabelecimento_p, 
			cd_tipo_receb_w, 
			nr_titulo_p, 
			nr_seq_trans_fin_w, 
			vl_baixa_p, 
			null,--dt_recebimento_p, 
			nm_usuario_p, 
			null,--vl_glosa_p, 
			null,--nr_bordero_p, 
			null,--nr_seq_conta_banco_p, 
			null,--vl_rec_maior_p); 
			null);--vl_perdas_p 
	end;
end if;
 
CALL atualizar_saldo_tit_rec(nr_titulo_p, nm_usuario_p);
 
--select do retorno 
select	coalesce(max(a.nr_sequencia),0) 
into STRICT	ds_retorno_w 
from	titulo_receber_liq a 
where	a.nr_titulo = nr_titulo_p;
 
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION baixa_titulo_receber_js (nr_titulo_p bigint, vl_baixa_p bigint, vl_juros_p bigint, vl_multa_p bigint, cd_estabelecimento_p bigint,  nm_usuario_p text) FROM PUBLIC;

