CREATE OR REPLACE FUNCTION biob_obter_se_mapa_usado ( nr_seq_mapa_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registros_w	bigint;
ds_retorno_w	varchar(1) := 'N';


BEGIN

if (nr_seq_mapa_p IS NOT NULL AND nr_seq_mapa_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_registros_w
	from	biob_coleta_tubo
	where	nr_seq_mapa_loc = nr_seq_mapa_p;

	if (qt_registros_w > 0) then
		ds_retorno_w := 'S';
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
