CREATE OR REPLACE FUNCTION biob_obter_se_mat_freezer ( nr_seq_doador_p bigint, nr_seq_mapa_p bigint, nr_seq_tipo_mat_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_freezer_w	bigint;
nr_seq_freezer_ww	bigint;
nr_seq_tipo_mat_w	bigint;
ds_retorno_w	varchar(1) := 'N';

C01 CURSOR FOR
	SELECT (	SELECT	c.nr_seq_freezer
			from	biob_mapa_localizacao c
			where	c.nr_sequencia = a.nr_seq_mapa_loc) nr_seq_freezer,
		a.nr_seq_tipo_mat
	from	biob_coleta_tubo a
	where	a.nr_seq_coleta in (	select	b.nr_sequencia
					from	biob_doador_coleta b
					where	b.nr_seq_doador = nr_seq_doador_p)
	and	(a.nr_seq_mapa_loc IS NOT NULL AND a.nr_seq_mapa_loc::text <> '')
	order by	1;


BEGIN

if ((nr_seq_doador_p IS NOT NULL AND nr_seq_doador_p::text <> '') and (nr_seq_mapa_p IS NOT NULL AND nr_seq_mapa_p::text <> '') and (nr_seq_tipo_mat_p IS NOT NULL AND nr_seq_tipo_mat_p::text <> '')) then
	begin
	open C01;
	loop
	fetch C01 into
		nr_seq_freezer_w,
		nr_seq_tipo_mat_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	max(a.nr_seq_freezer)
		into STRICT	nr_seq_freezer_ww
		from	biob_mapa_localizacao a
		where	a.nr_sequencia = nr_seq_mapa_p;

		if (nr_seq_freezer_w = nr_seq_freezer_ww AND nr_seq_tipo_mat_p = nr_seq_tipo_mat_w) then
			ds_retorno_w := 'S';
		end if;
		end;
	end loop;
	close C01;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
