CREATE OR REPLACE FUNCTION bkf_obter_conv_externa ( cd_cgc_p text, nm_tabela_p text, nm_atributo_p text, cd_interno_p text, ie_sistema_externo_p text, nr_seq_regra_p bigint default null) RETURNS varchar AS $body$
DECLARE


/*Utilizada pelo backoffice para trazer a convers√£o externa.*/

cd_externo_w			conversao_meio_externo.cd_interno%type;


BEGIN
cd_externo_w			:= '';
begin
select	max(cd_externo)
into STRICT	cd_externo_w
from	conversao_meio_externo
where	((coalesce(cd_cgc_p::text, '') = '') or (cd_cgc = cd_cgc_p))
and	upper(nm_tabela)	= upper(nm_tabela_p)
and	upper(nm_atributo)	= upper(nm_atributo_p)
and	((ie_sistema_externo	= ie_sistema_externo_p) or (coalesce(ie_sistema_externo_p::text, '') = ''))
and	cd_interno		= cd_interno_p
and	nr_seq_regra 		= coalesce(nr_seq_regra_p,nr_seq_regra);
exception
    		when others then
		cd_externo_w := '';
end;

return cd_externo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
