CREATE OR REPLACE FUNCTION bkf_obter_conv_interna ( cd_cgc_p text, nm_tabela_p text, nm_atributo_p text, cd_externo_p text, ie_sistema_externo_p text, nr_seq_regra_p bigint default null) RETURNS varchar AS $body$
DECLARE


/*Utilizada pelo backoffice para trazer a conversao interna.*/

cd_interno_w			conversao_meio_externo.cd_interno%type;
cd_cgc_w			conversao_meio_externo.cd_cgc%type;


BEGIN
cd_interno_w			:=	'';
cd_cgc_w			:=	cd_cgc_p;

if (ish_param_pck.get_se_regra_ish(nr_seq_regra_p) = 'S') then
	begin
	begin
	select	cd_cgc
	into STRICT	cd_cgc_w
	from	pessoa_juridica
	where	cd_cgc = cd_cgc_w;
	exception
	when others then
		select	cd_cgc
		into STRICT	cd_cgc_w
		from	estabelecimento
		where	cd_estabelecimento = obter_estabelecimento_ativo;
	end;	
	
	select	max(cd_interno)
	into STRICT	cd_interno_w
	from	conversao_meio_externo
	where	((coalesce(cd_cgc_w::text, '') = '') or (coalesce(cd_cgc, cd_cgc_w) = cd_cgc_w))
	and	upper(nm_tabela)	= upper(nm_tabela_p)
	and	upper(nm_atributo)	= upper(nm_atributo_p)
	and	((ie_sistema_externo	= ie_sistema_externo_p) or (coalesce(ie_sistema_externo_p::text, '') = ''))
	and	cd_externo		= cd_externo_p
	and	nr_seq_regra 		= coalesce(nr_seq_regra_p,nr_seq_regra);
	exception
	when others then
		cd_interno_w := '';
	end;
else
	begin
	select	max(cd_interno)
	into STRICT	cd_interno_w
	from	conversao_meio_externo
	where	((coalesce(cd_cgc_w::text, '') = '') or (cd_cgc = cd_cgc_w))
	and	upper(nm_tabela)		= upper(nm_tabela_p)
	and	upper(nm_atributo)		= upper(nm_atributo_p)
	and	((ie_sistema_externo	= ie_sistema_externo_p) or (coalesce(ie_sistema_externo_p::text, '') = ''))
	and	cd_externo		= cd_externo_p
	and	nr_seq_regra 		= coalesce(nr_seq_regra_p,nr_seq_regra);
	exception
			when others then
			cd_interno_w := '';
	end;
end if;

return cd_interno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
