CREATE OR REPLACE FUNCTION bl_obter_classif_coleta (nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


dt_parto_w		timestamp;
ie_classificacao_w	varchar(1);


BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	select	max(dt_parto)
	into STRICT	dt_parto_w
	from	bl_parto
	where	nr_seq_banco_leite = nr_seq_doacao_p;

	if (dt_parto_w IS NOT NULL AND dt_parto_w::text <> '') then
		select	max(ie_classificacao)
		into STRICT	ie_classificacao_w
		from	bl_regra_classif_leite
		where	ceil(clock_timestamp() - dt_parto_w) between qt_dias_min and qt_dias_max;
	end if;
end if;

return	ie_classificacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
