CREATE OR REPLACE FUNCTION bl_obter_classif_leite (nr_seq_banco_leite_p bigint) RETURNS varchar AS $body$
DECLARE


qt_idade_gestacional_w	integer;
ie_classificacao_w	varchar(1);


BEGIN
if (nr_seq_banco_leite_p IS NOT NULL AND nr_seq_banco_leite_p::text <> '') then

	select	coalesce(max(qt_sem_ig_cronologica),0)
	into STRICT	qt_idade_gestacional_w
	from	bl_parto
	where	nr_seq_banco_leite = nr_seq_banco_leite_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	select	max(ie_classificacao)
	into STRICT	ie_classificacao_w
	from	bl_regra_classif_leite
	where	qt_idade_gestacional_w between coalesce(qt_sem_ig_cronologica_min,0) and coalesce(qt_sem_ig_cronologica_max,999);

end if;

return	ie_classificacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
