CREATE OR REPLACE FUNCTION bl_obter_cor_ciclo ( nr_sequencia_p bigint, ie_atributo_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_w		varchar(20);
nr_seq_regra_medicao_w	bigint;
qt_valor_w		real;
qt_valor_minimo_w	real;
qt_valor_maximo_w	real;
nr_seq_ciclo_w		bigint;
nm_atributo_w		varchar(255);
ie_valor_ok_w		varchar(1) := 'S';


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_atributo_p IS NOT NULL AND ie_atributo_p::text <> '') then

	nm_atributo_w := 'NR_SEQ_RESULTADO'||ie_atributo_p;
	nr_seq_ciclo_w := obter_valor_dinamico('select ' || nm_atributo_w ||
			' 	from w_bl_ciclo
				where nm_usuario = ' ||chr(39)||nm_usuario_p||chr(39)||
			' 	and nr_sequencia = ' ||chr(39)||nr_sequencia_p||chr(39), nr_seq_ciclo_w);

	if (nr_seq_ciclo_w IS NOT NULL AND nr_seq_ciclo_w::text <> '') then
		select	max(a.ds_cor)
		into STRICT	ds_cor_w
		from	bl_regra_ciclo a,
			bl_ciclo b
		where	a.ie_fase = b.ie_fase
		and	b.nr_sequencia = nr_seq_ciclo_w;

		select	max(qt_valor)
		into STRICT	qt_valor_w
		from	bl_ciclo
		where	nr_sequencia = nr_seq_ciclo_w;

		if (qt_valor_w IS NOT NULL AND qt_valor_w::text <> '') then
			ie_valor_ok_w := BL_obter_se_valor_ok(nr_seq_ciclo_w,qt_valor_w);
		end if;
	end if;

	ds_cor_w := ds_cor_w||ie_valor_ok_w;
end if;

return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
