SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bl_obter_dados_ciclo ( nr_seq_ciclo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
J - Justificativa
V - Valor
*/
ds_retorno_w		varchar(255);
ds_justificativa_w	varchar(255);
qt_valor_w		real;


BEGIN
if (nr_seq_ciclo_p IS NOT NULL AND nr_seq_ciclo_p::text <> '') then

	select	ds_justificativa,
		qt_valor
	into STRICT	ds_justificativa_w,
		qt_valor_w
	from	bl_ciclo
	where	nr_sequencia = nr_seq_ciclo_p;

	if (ie_opcao_p = 'J') then
		ds_retorno_w := ds_justificativa_w;
	elsif (ie_opcao_p = 'V') then
		ds_retorno_w := qt_valor_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
