CREATE OR REPLACE FUNCTION bl_obter_dados_parametro (cd_estabelecimento_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

/* ie_tipo_p
A = agenda
V = Volume mínimo congelamento
M - Motivo inutilização
*/
ds_retorno_w		varchar(255);
cd_agenda_w		varchar(10);
qt_volume_inutil_w	bigint;
nr_seq_motivo_inutil_w	bigint;


BEGIN

select 	max(cd_agenda),
	max(qt_volume_inutil),
	max(nr_seq_motivo_inutil)
into STRICT	cd_agenda_w,
	qt_volume_inutil_w,
	nr_seq_motivo_inutil_w
from	bl_parametro
where	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p;

if (ie_tipo_p = 'A') then
	ds_retorno_w := cd_agenda_w;
elsif (ie_tipo_p = 'V') then
	ds_retorno_w := qt_volume_inutil_w;
elsif (ie_tipo_p = 'M') then
	ds_retorno_w := nr_seq_motivo_inutil_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
