SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bl_obter_desc_status ( nr_seq_frasco_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_status_leite_w	bigint;


BEGIN
if (nr_seq_frasco_p IS NOT NULL AND nr_seq_frasco_p::text <> '') then

	select	nr_seq_status_leite
	into STRICT	nr_seq_status_leite_w
	from	bl_frasco
	where	nr_sequencia = nr_seq_frasco_p;

	select	max(b.ds_valor_dominio)
	into STRICT	ds_retorno_w
	from	bl_status_leite a,
		valor_dominio b
	where	a.ie_status 	= b.vl_dominio
	and	nr_sequencia 	= nr_seq_status_leite_w
	and	b.cd_dominio	= 5657;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
