CREATE OR REPLACE FUNCTION bl_obter_resultado_exame ( nr_seq_exame_lote_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

SELECT 	CASE WHEN b.ie_tipo_resultado='N' THEN  TO_CHAR(a.vl_resultado)  ELSE CASE WHEN a.ds_resultado='' THEN a.ds_resultado  ELSE CASE WHEN a.ds_resultado='N' THEN  Wheb_MENSAGEM_PCK.GET_TEXTO(803578)  ELSE Wheb_MENSAGEM_PCK.GET_TEXTO(803579) END  END  END
into STRICT ds_retorno_w
FROM	bl_exame b,
	bl_exame_realizado a
WHERE a.nr_seq_exame		= b.nr_sequencia
  AND a.nr_seq_lote_exame	= nr_seq_exame_lote_p
  AND a.nr_seq_exame		= nr_seq_exame_p;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
