CREATE OR REPLACE FUNCTION bl_obter_result_exame_concat ( nr_seq_exame_lote_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

SELECT 	CASE WHEN b.ie_tipo_resultado='N' THEN  TO_CHAR(a.vl_resultado)  ELSE CASE WHEN coalesce(a.ds_resultado,'')='N' THEN 'Negativo'  ELSE CASE WHEN UPPER(a.ds_resultado)='NEGATIVO' THEN 'Negativo'  ELSE 'Positivo' END  END  END
into STRICT	ds_retorno_w
from	bl_exame b,
  bl_exame_realizado a
where a.nr_seq_exame		= b.nr_sequencia
  and a.nr_seq_lote_exame	= nr_seq_exame_lote_p
  and a.nr_seq_exame		= nr_seq_exame_p;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
