SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bl_obter_se_agenda_dia (nr_seq_doacao_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);


BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	select	cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	bl_doacao
	where	nr_sequencia = nr_seq_doacao_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	agenda_consulta
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
	and	cd_agenda = bl_obter_dados_parametro(cd_estabelecimento_p,'A')
	and	dt_agenda between trunc(clock_timestamp()) and fim_dia(clock_timestamp());

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
