CREATE OR REPLACE FUNCTION bl_obter_se_analise_ok (nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := 'S';
nr_seq_item_w		bigint;
vl_item_w		integer;
vl_minimo_w		integer;
vl_maximo_w		integer;

C01 CURSOR FOR
	SELECT	a.nr_seq_item,
		a.vl_item
	from	bl_item_analise_frasco a,
		bl_lote_analise_frasco b,
		bl_lote_item c,
		bl_frasco d
	where	a.nr_seq_lote 	= b.nr_sequencia
	and	b.nr_frasco 	= c.nr_seq_frasco
	and	b.nr_seq_lote_prod = nr_seq_lote_p
	and	c.nr_seq_frasco	= d.nr_sequencia
	and	coalesce(d.dt_inutilizacao::text, '') = ''
	order by 1;


BEGIN
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_seq_item_w,
		vl_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	vl_minimo,
			vl_maximo
		into STRICT	vl_minimo_w,
			vl_maximo_w
		from	bl_analise_frasco
		where	nr_sequencia = nr_seq_item_w;

		if (vl_item_w < vl_minimo_w) or (vl_item_w > vl_maximo_w) then
			ds_retorno_w := 'N';
		end if;

		end;
	end loop;
	close C01;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
