CREATE OR REPLACE FUNCTION bl_obter_se_valor_ok ( nr_seq_ciclo_p bigint, qt_valor_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
nr_seq_regra_medicao_w	bigint;
qt_valor_minimo_w	real;
qt_valor_maximo_w	real;


BEGIN
if (nr_seq_ciclo_p IS NOT NULL AND nr_seq_ciclo_p::text <> '') and (qt_valor_p IS NOT NULL AND qt_valor_p::text <> '') then

	select	max(c.nr_sequencia)
	into STRICT	nr_seq_regra_medicao_w
	from	bl_ciclo b,
		bl_regra_medicao c
	where	b.nr_seq_variavel = c.nr_seq_variavel
	and	b.nr_sequencia = nr_seq_ciclo_p
	and	c.ie_fase = b.ie_fase;

	if (nr_seq_regra_medicao_w IS NOT NULL AND nr_seq_regra_medicao_w::text <> '') then

		select	qt_valor_minimo,
			qt_valor_maximo
		into STRICT	qt_valor_minimo_w,
			qt_valor_maximo_w
		from	bl_regra_medicao
		where	nr_sequencia = nr_seq_regra_medicao_w;

		if (qt_valor_p < qt_valor_minimo_w) or (qt_valor_p > qt_valor_maximo_w) then
			ds_retorno_w := 'N';
		end if;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
