CREATE OR REPLACE FUNCTION bl_obter_situacao_frasco (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
dt_frasco_w		timestamp;
qt_tempo_status_w	bigint;
ie_tipo_tempo_w		varchar(1);
qt_tempo_aviso_w	bigint;
ie_tipo_aviso_w		varchar(1);
nr_seq_status_leite_w	bigint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	nr_seq_status_leite,
		dt_frasco
	into STRICT	nr_seq_status_leite_w,
		dt_frasco_w
	from	bl_frasco
	where	nr_sequencia = nr_sequencia_p;

	if (nr_seq_status_leite_w IS NOT NULL AND nr_seq_status_leite_w::text <> '') then

		select	qt_tempo_status,
			ie_tipo_tempo,
			qt_tempo_aviso,
			ie_tipo_aviso
		into STRICT	qt_tempo_status_w,
			ie_tipo_tempo_w,
			qt_tempo_aviso_w,
			ie_tipo_aviso_w
		from	bl_status_leite
		where	nr_sequencia = nr_seq_status_leite_w;

		if	((ie_tipo_aviso_w = 'H') and ((dt_frasco_w + (qt_tempo_aviso_w/24)) < clock_timestamp())) or
			((ie_tipo_aviso_w = 'D') and ((dt_frasco_w + qt_tempo_aviso_w) < clock_timestamp())) or
			((ie_tipo_aviso_w = 'M') and (PKG_DATE_UTILS.ADD_MONTH(dt_frasco_w,qt_tempo_aviso_w,0) < clock_timestamp())) then
			ds_retorno_w := 'A';
		end if;

		if	((ie_tipo_tempo_w = 'H') and ((dt_frasco_w + (qt_tempo_status_w/24)) < clock_timestamp())) or
			((ie_tipo_tempo_w = 'D') and ((dt_frasco_w + qt_tempo_status_w) < clock_timestamp())) or
			((ie_tipo_tempo_w = 'M') and (PKG_DATE_UTILS.ADD_MONTH(dt_frasco_w,qt_tempo_status_w,0) < clock_timestamp())) then
			ds_retorno_w := 'V';
		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
