SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bl_obter_vol_atendido (nr_seq_nut_prod_p bigint) RETURNS bigint AS $body$
DECLARE

			
qt_atendida_w		bigint;
ie_leite_materno_w	varchar(1);
nr_prescricao_w		bigint;
nr_seq_prod_lac_w	bigint;
dt_horario_w		timestamp;


BEGIN
if (nr_seq_nut_prod_p IS NOT NULL AND nr_seq_nut_prod_p::text <> '') then
	
	begin
	select	'S'
	into STRICT	ie_leite_materno_w
	from	nut_prod_lac_item a,
		nutricao_leite_deriv b,
		classif_leite_deriv c
	where	b.cd_material = a.cd_material
	and	b.nr_seq_classif = c.nr_sequencia
	and (b.ie_situacao = 'A' or coalesce(b.ie_situacao::text, '') = '')
	and	a.nr_seq_prod_princ = nr_seq_nut_prod_p
	and	c.ie_leite_materno = 'S'  LIMIT 1;
	exception
	WHEN data_exception THEN
		ie_leite_materno_w := 'N';
	end;
	
	if (ie_leite_materno_w = 'S') then
		select	coalesce(sum(qt_volume),0)
		into STRICT	qt_atendida_w
		from	nut_prod_lac_item a,
			bl_frasco b
		where	a.nr_sequencia = b.nr_seq_prod_item
		and  	a.nr_seq_prod_princ = nr_seq_nut_prod_p;
	else
		select	nr_prescricao,
			nr_sequencia,
			dt_horario
		into STRICT	nr_prescricao_w,
			nr_seq_prod_lac_w,
			dt_horario_w
		from	nut_prod_lac_item
		where	nr_seq_prod_princ = nr_seq_nut_prod_p;
		
		select	coalesce(sum(b.qt_volume),0)
		into STRICT	qt_atendida_w
		from	nut_prod_lac_item a,
			bl_frasco b
		where	a.nr_sequencia 	= b.nr_seq_prod_item
		and	a.nr_prescricao	= nr_prescricao_w
		and	a.nr_sequencia = nr_seq_prod_lac_w
		and	a.dt_horario	= dt_horario_w;
	end if;
end if;

return	qt_atendida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION bl_obter_vol_atendido (nr_seq_nut_prod_p bigint) FROM PUBLIC;

