CREATE OR REPLACE FUNCTION brasindice_carga_exec_count ( cd_consulta_p text, cd_laboratorio_p text, cd_medicamento_p text, cd_apresentacao_p text, dt_inicio_vigencia_p timestamp, ie_tipo_preco_p text, ie_tipo_acao_p text, ds_estabelecimentos_p text, cd_estabelecimento_p bigint ) RETURNS bigint AS $body$
DECLARE


-- Function used at Brasindice Import (Cargas) due to use of DAO (only has methods to call querys by static String).
vl_retorno_w	brasindice_preco.nr_sequencia%type := 0;


BEGIN

CASE
   WHEN cd_consulta_p = '189605' THEN

	select	count(distinct ie_tipo_preco) qt_registros
	into STRICT	vl_retorno_w
	from	brasindice_preco
	where	cd_laboratorio = cd_laboratorio_p
	and	cd_medicamento = cd_medicamento_p
	and	cd_apresentacao= cd_apresentacao_p
	and	ie_tipo_preco  in ('PMC','PFB')
	and	dt_inicio_vigencia = dt_inicio_vigencia_p;

   WHEN cd_consulta_p = '246327' THEN
	
	select 	count(1)
	into STRICT	vl_retorno_w
	from 	brasindice_preco
	where 	cd_laboratorio = cd_laboratorio_p
	and   	cd_medicamento = cd_medicamento_p    
	and   	cd_apresentacao= cd_apresentacao_p   
	and   	ie_tipo_preco  = ie_tipo_preco_p      
	and   	(((ie_tipo_acao_p = '1') 
	and (obter_se_contido(cd_estabelecimento, elimina_aspas(ds_estabelecimentos_p)) = 'S')) 
	or   	((ie_tipo_acao_p = '2') 
	and (not obter_se_contido(cd_estabelecimento, elimina_aspas(ds_estabelecimentos_p)) = 'S')))
	and   	dt_inicio_vigencia = dt_inicio_vigencia_p;

   WHEN cd_consulta_p = '189578' THEN
	
	select	count(1)
	into STRICT	vl_retorno_w
	from	brasindice_preco
	where	cd_laboratorio	= cd_laboratorio_p
	and	cd_medicamento	= cd_medicamento_p
	and	cd_apresentacao	= cd_apresentacao_p
	and	ie_tipo_preco	= ie_tipo_preco_p
	and     coalesce(cd_estabelecimento, cd_estabelecimento_p, 0) = coalesce(cd_estabelecimento_p, 0)
	and	dt_inicio_vigencia	= dt_inicio_vigencia_p;

   WHEN cd_consulta_p = '950998' THEN
	
	select 	count(1)
	into STRICT	vl_retorno_w
	from	brasindice_preco
	where	cd_laboratorio	= cd_laboratorio_p 
	and	cd_medicamento	= cd_medicamento_p 
	and	cd_apresentacao	= cd_apresentacao_p 
	and	ie_tipo_preco	= ie_tipo_preco_p 
	and	dt_inicio_vigencia	= dt_inicio_vigencia_p;

   ELSE 
	vl_retorno_w	:= 0;
END CASE;


return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
