SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bsc_cmo_kpis (ds_type_p text, dt_initial_p text, dt_final_p text) RETURNS bigint AS $body$
DECLARE


nr_result_w		double precision;
qt_time_w		bigint;
qt_time_type_w		bigint;
qt_so_w			bigint;
qt_so_satisfactory_w	bigint;
qt_hours_month_w	double precision;


BEGIN
/*
	Parâmetros utilizados para o "ds_type_p"

 IPR - percentage of activity types including CMO innovation
 SSS - percentage of activity types dedicated to marketing, sales and service support
 TCMO - Number of registered hours of CMO activities according to the types in service orders (total)
 PCMO - Number of registered houes of CMO activities according to the types in service orders (percentage)
 SLVL - Level of satisfaction during the closure of the SO
 CFR - Number of hours for de first CMO positioning after the opening of the service order and/or the request

	Tipos de atividades realizadas calculadas

1412 - CMO - Innovation
1413 - CMO - Philips Research
1414 - CMO - System Maintenance
1415 - CMO - Services Support
1416 - CMO - Sales and Marketing Support
1417 - CMO - Product Management Support
1418 - CMO - QR

*/
	nr_result_w := 0;

	if (ds_type_p = 'IPR') then
		select  sum(b.qt_minuto)
		into STRICT	qt_time_w
		from    man_ordem_serv_ativ b
		where   b.nr_seq_funcao in (1412, 1413, 1414, 1415, 1416, 1417, 1418)
		and	b.dt_atividade between dt_initial_p and fim_mes(dt_final_p);

		select  sum(b.qt_minuto)
		into STRICT	qt_time_type_w
		from    man_ordem_serv_ativ b
		where   b.nr_seq_funcao in (1412)
		and	b.dt_atividade between dt_initial_p and fim_mes(dt_final_p);		

		nr_result_w := ((qt_time_type_w * 100) / qt_time_w);

	elsif (ds_type_p = 'SSS') then
		select  sum(b.qt_minuto)
		into STRICT	qt_time_w
		from    man_ordem_serv_ativ b
		where   b.nr_seq_funcao in (1412, 1413, 1414, 1415, 1416, 1417, 1418)
		and	b.dt_atividade between dt_initial_p and fim_mes(dt_final_p);

		select  sum(b.qt_minuto)
		into STRICT	qt_time_type_w
		from    man_ordem_serv_ativ b
		where   b.nr_seq_funcao in (1415, 1416)
		and	b.dt_atividade between dt_initial_p and fim_mes(dt_final_p);		

		nr_result_w := ((qt_time_type_w * 100) / qt_time_w);

	elsif (ds_type_p = 'TCMO') then		/* Total do valor de horas */
		select  sum(b.qt_minuto)
		into STRICT	qt_time_w
		from    man_ordem_serv_ativ b
		where   b.nr_seq_funcao in (1412, 1413, 1414, 1415, 1416, 1417, 1418)
		and	b.dt_atividade between dt_initial_p and fim_mes(dt_final_p);
		
		nr_result_w := (qt_time_w / 60);

	elsif (ds_type_p = 'PCMO') then		/*  Percentual do valor de horas */
		select  sum(b.qt_minuto)
		into STRICT	qt_time_w
		from    man_ordem_serv_ativ b
		where   b.nr_seq_funcao in (1412, 1413, 1414, 1415, 1416, 1417, 1418)
		and	b.dt_atividade between dt_initial_p and fim_mes(dt_final_p);

		qt_hours_month_w := obter_dias_uteis_periodo(dt_initial_p, dt_final_p, 1) * 24.6; --Informado pelo Dr. Paulo: 24,6 corresponde ao total de horas possíveis de registro por dia de trabalho da equipe CMO.
		
		nr_result_w := ((qt_time_w / 60) * 100) / qt_hours_month_w;

	elsif (ds_type_p = 'SLVL') then
		select  sum(1)
		into STRICT	qt_so_w
		from    man_ordem_servico a
		where   exists (SELECT 1
			from   man_ordem_serv_ativ b
			where  b.nr_seq_ordem_serv = a.nr_sequencia
			and    b.nr_seq_funcao in (1412, 1413, 1414, 1415, 1416, 1417, 1418)
			and    b.dt_atividade between dt_initial_p and fim_mes(dt_final_p))
		and	a.ie_status_ordem = 3;	

		select  sum(1)
		into STRICT	qt_so_satisfactory_w
		from    man_ordem_servico a
		where   exists (SELECT 1
			from   man_ordem_serv_ativ b
			where  b.nr_seq_ordem_serv = a.nr_sequencia
			and    b.nr_seq_funcao in (1412, 1413, 1414, 1415, 1416, 1417, 1418)
			and    b.dt_atividade between dt_initial_p and fim_mes(dt_final_p))
		and	a.ie_grau_satisfacao in ('O', 'B', 'N')
		and	a.ie_status_ordem = 3;

		nr_result_w := ((qt_so_satisfactory_w * 100) / qt_so_w);

	elsif (ds_type_p = 'CFR') then--estágio até histórico do CMO
		select	coalesce(sum(man_obter_tempo_com(wheb_usuario_pck.get_cd_estabelecimento(),
				dt_estagio_cmo, 
				coalesce((	select		min(dt_liberacao) 
						from		man_ordem_serv_tecnico t 
						where		t.nr_seq_ordem_serv = x.nr_seq_ordem 
						and			dt_liberacao > dt_estagio_cmo 
						and			t.nm_usuario in (	select	Obter_usuario_pf(cd_pessoa_fisica) 
															from   ccb_equipe_regra 
															where  nr_seq_equipe = 1)), clock_timestamp()), 'MC')), 0) / 60 qt
				into STRICT	nr_result_w													
				from (	SELECT	nr_seq_ordem, 
									dt_atualizacao dt_estagio_cmo 
							from	man_ordem_serv_estagio a 
							where	a.nr_seq_estagio =  2283 
							and		a.dt_atualizacao between dt_initial_p and Fim_mes(dt_final_p)) x;
	end if;


return	nr_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION bsc_cmo_kpis (ds_type_p text, dt_initial_p text, dt_final_p text) FROM PUBLIC;

