SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bsc_obter_consolidado ( nr_seq_indicador_p bigint, nr_seq_inf_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
Meta
Limite
Real
*/
cd_ano_w			smallint;
cd_periodo_w			smallint;
cd_empresa_w			bigint;
ie_forma_consol_empresa_w		varchar(15);
qt_meta_w			double precision	:= 0;
qt_limite_w			double precision	:= 0;
qt_real_w				double precision	:= 0;
vl_retorno_w			double precision	:= 0;


BEGIN

select	coalesce(max(ie_forma_consol_empresa),'N')
into STRICT	ie_forma_consol_empresa_w
from	bsc_indicador
where	nr_sequencia	= nr_seq_indicador_p;

if (ie_forma_consol_empresa_w <> 'N') then
	begin
	select	max(cd_empresa),
		max(cd_ano),
		max(cd_periodo)
	into STRICT	cd_empresa_w,
		cd_ano_w,
		cd_periodo_w
	from	bsc_ind_inf
	where	nr_sequencia	= nr_seq_inf_p;

	if (ie_forma_consol_empresa_w = 'SOMA') then
		begin
		select	coalesce(sum(qt_meta),0),
			coalesce(sum(qt_limite),0),
			coalesce(sum(qt_real),0)
		into STRICT	qt_meta_w,
			qt_limite_w,
			qt_real_w
		from	bsc_ind_inf
		where	cd_empresa	= cd_empresa_w
		and	cd_ano		= cd_ano_w
		and	cd_periodo	= cd_periodo_w
		and	nr_seq_indicador= nr_seq_indicador_p;
		end;
	elsif (ie_forma_consol_empresa_w = 'MEDIA') then
		begin
		select	coalesce(avg(qt_meta),0),
			coalesce(avg(qt_limite),0),
			coalesce(avg(qt_real),0)
		into STRICT	qt_meta_w,
			qt_limite_w,
			qt_real_w
		from	bsc_ind_inf
		where	cd_empresa	= cd_empresa_w
		and	cd_ano		= cd_ano_w
		and	cd_periodo	= cd_periodo_w
		and	nr_seq_indicador= nr_seq_indicador_p;
		end;
	end if;
	end;
end if;

if (ie_opcao_p = 'M') then
	vl_retorno_w	:= qt_meta_w;
elsif (ie_opcao_p = 'L') then
	vl_retorno_w	:= qt_limite_w;
elsif (ie_opcao_p = 'R') then
	vl_retorno_w	:= qt_real_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
