CREATE OR REPLACE FUNCTION bsc_obter_cor_fonte_tendencia ( cd_empresa_p bigint, nr_seq_indicador_p bigint, cd_ano_p bigint, pr_variacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cor_fonte_w				varchar(100);
ds_cor_fundo_w				varchar(100);
ds_cor_retorno_w			varchar(100);
nr_seq_indicador_w			bigint	:= nr_seq_indicador_p;
pr_variacao_w				double precision	:= pr_variacao_p;
ie_regra_result_w			varchar(15);

c01 CURSOR FOR
SELECT	a.ds_cor_fundo,
	a.ds_cor_fonte
from	BSC_REGRA_VAR_TENDENCIA a
where	a.cd_empresa	= cd_empresa_p
and	coalesce(a.nr_seq_indicador, nr_seq_indicador_w) = nr_seq_indicador_w
and	pr_variacao_w between a.pr_valor_min and a.pr_valor_max
and	coalesce(a.ie_regra_result, ie_regra_result_w) = ie_regra_result_w
order by 	coalesce(nr_seq_indicador,0),
		coalesce(a.ie_regra_result,'X');


BEGIN

select	coalesce(ie_regra_result,'X')
into STRICT	ie_regra_result_w
from	bsc_indicador
where	nr_sequencia = nr_seq_indicador_p;

open C01;
loop
fetch C01 into
	ds_cor_fundo_w,
	ds_cor_fonte_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_cor_retorno_w	:= ds_cor_fonte_w;
	end;
end loop;
close C01;

return	coalesce(ds_cor_retorno_w,'clNavy');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
