CREATE OR REPLACE FUNCTION bsc_obter_cor_indicador (nr_sequencia_p bigint, nr_seq_inf_p bigint, cd_ano_p bigint, cd_periodo_p bigint) RETURNS varchar AS $body$
DECLARE


qt_meta_w			bsc_ind_inf.qt_meta%type;
qt_limite_w			bsc_ind_inf.qt_limite%type;
qt_real_w			bsc_ind_inf.qt_real%type;
ie_informado_w			bsc_ind_inf.ie_informado%type;
nr_seq_result_w			bsc_ind_inf.nr_seq_result%type;
ie_regra_result_w		bsc_indicador.ie_regra_result%type;
ds_color_w			varchar(20);


BEGIN

begin
select  qt_meta,
        qt_limite,
        qt_real,
        a.ie_regra_result,
	b.ie_informado,
	b.nr_seq_result
into STRICT	qt_meta_w,
	qt_limite_w,
	qt_real_w,
	ie_regra_result_w,
	ie_informado_w,
	nr_seq_result_w
from    bsc_indicador a,
        bsc_ind_inf b
where   a.nr_sequencia  = b.nr_seq_indicador
and     a.nr_sequencia  = nr_sequencia_p
and     b.cd_ano        = cd_ano_p
and     bsc_obter_sql_periodo_mapa(b.cd_periodo,a.nr_sequencia,cd_periodo_p) = 'S'
and	b.nr_sequencia	= nr_seq_inf_p;
exception when others then
	ie_regra_result_w	:= '';
end;

if ((nr_seq_result_w IS NOT NULL AND nr_seq_result_w::text <> '') and (ie_informado_w = 'S' or OBTER_PARAM_USUARIO_LOGADO(7024, 108) = 'N')) then
	begin
	if (ie_regra_result_w = 'MA') then

		if (qt_real_w >= qt_meta_w) then
			ds_color_w	:=	'$0080FF80'; -- Verde
		elsif (qt_real_w < qt_limite_w) then
			ds_color_w	:=	'$008080FF'; -- Vermelho
		else
			ds_color_w	:=	'$0080FFFF'; -- Amarelo
		end	if;
	elsif (ie_regra_result_w = 'N') then
		begin
			ds_color_w	:=	'$0080FF80'; -- Verde
		end;
	else
		begin
		if (qt_real_w <= qt_meta_w) then
			ds_color_w	:=	'$0080FF80'; -- Verde
		elsif (qt_real_w > qt_limite_w) then
			ds_color_w	:=	'$008080FF'; -- Vermelho
		else
			ds_color_w	:=	'$0080FFFF'; -- Amarelo
		end	if;
		end;
	end	if;
	end;
end if;

return	ds_color_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
