SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bsc_obter_cor_tipo_doc ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
FO - Fonte
FU - Fundo*/
ds_retorno_w	varchar(15);
ds_cor_fonte_w	varchar(15);
ds_cor_fundo_w	varchar(15);


BEGIN
select	max(a.ds_cor_fonte),
	max(a.ds_cor_fundo)
into STRICT	ds_cor_fonte_w,
	ds_cor_fundo_w
from	bsc_tipo_documento a
where	a.nr_sequencia = coalesce(nr_sequencia_p,0);

if (ie_opcao_p = 'FO') then
	ds_retorno_w := ds_cor_fonte_w;
elsif (ie_opcao_p = 'FU') then
	ds_retorno_w := ds_cor_fundo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
