SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bsc_obter_dados_indic ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*IE_OPCAO

CL = CLASSIFICACAO
RS = REGRA RESULTADO
OBJ = Trazer o objetivo do indicador
PER = Trazer a perspectiva do indicador
PERC = Trazer a perspectiva do indicador
*/
ds_retorno_w			varchar(4000);
nr_seq_objetivo_w		bigint;
nr_seq_perspectiva_w	bigint;


BEGIN

if (coalesce(nr_sequencia_p,0) <> 0) then
	begin
	if (ie_opcao_p = 'CL') then
		select	substr(max(cd_classificacao),1,40)
		into STRICT	ds_retorno_w
		from	bsc_indicador
		where	nr_sequencia	= nr_sequencia_p;
	end if;

	end;
end if;

if (coalesce(nr_sequencia_p,0) <> 0) then
	begin
	if (ie_opcao_p = 'RS') then
		select 	max(ie_regra_result)
		into STRICT	ds_retorno_w
		from	bsc_indicador
		where 	nr_sequencia = nr_sequencia_p;
	elsif (ie_opcao_p = 'OBJ') then
		begin
		select	max(nr_seq_objetivo)
		into STRICT	nr_seq_objetivo_w
		from	bsc_ind_obj
		where	nr_seq_indicador = nr_sequencia_p;

		select	coalesce(max(ds_titulo),max(ds_objetivo))
		into STRICT	ds_retorno_w
		from	ple_objetivo
		where	nr_sequencia	= nr_seq_objetivo_w;
		end;
	elsif (ie_opcao_p = 'PER') then
		begin
		select	max(nr_seq_objetivo)
		into STRICT	nr_seq_objetivo_w
		from	bsc_ind_obj
		where	nr_seq_indicador = nr_sequencia_p;

		select	max(b.ds_classificacao)
		into STRICT	ds_retorno_w
		from	ple_classificacao b,
				ple_objetivo a
		where	a.nr_seq_classif	= b.nr_sequencia
		and		a.nr_sequencia		= nr_seq_objetivo_w;
		end;
	elsif (ie_opcao_p = 'PERC') then
		begin
		select	max(nr_seq_objetivo)
		into STRICT	nr_seq_objetivo_w
		from	bsc_ind_obj
		where	nr_seq_indicador = nr_sequencia_p;

		select	max(b.nr_sequencia)
		into STRICT	ds_retorno_w
		from	ple_classificacao b,
				ple_objetivo a
		where	a.nr_seq_classif	= b.nr_sequencia
		and		a.nr_sequencia		= nr_seq_objetivo_w;
		end;
	end if;
	end;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
