CREATE OR REPLACE FUNCTION bsc_obter_dados_iniciativa ( nr_seq_os_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision	:= null;
qt_ativ_prev_w		bigint;
qt_ativ_concluida_w	bigint;
qt_ativ_pend_w		bigint;

/*
1 - %Conclus√£o Ativ Prev.
2 - %Atraso Ativ Prev
*/
BEGIN
if (nr_seq_os_p IS NOT NULL AND nr_seq_os_p::text <> '') then
	if (ie_opcao_p = 1) then
		select	count(*)
		into STRICT	qt_ativ_prev_w
		from	man_ordem_ativ_prev a
		where	a.nr_seq_ordem_serv	= nr_seq_os_p;

		select	count(*)
		into STRICT	qt_ativ_concluida_w
		from	man_ordem_ativ_prev a
		where	a.nr_seq_ordem_serv	= nr_seq_os_p
		and (a.pr_atividade		= 100 or (a.dt_real IS NOT NULL AND a.dt_real::text <> ''));

		vl_retorno_w	:=	dividir_sem_round(qt_ativ_concluida_w,qt_ativ_prev_w) * 100;
	elsif (ie_opcao_p = 2) then
		select	count(*)
		into STRICT	qt_ativ_prev_w
		from	man_ordem_ativ_prev a
		where	a.nr_seq_ordem_serv	= nr_seq_os_p;

		select	count(*)
		into STRICT	qt_ativ_concluida_w
		from	man_ordem_ativ_prev a
		where	a.nr_seq_ordem_serv	= nr_seq_os_p
		and (coalesce(a.pr_atividade,0)	< 100 or coalesce(a.dt_real::text, '') = '');

		vl_retorno_w	:=	dividir_sem_round(qt_ativ_pend_w,qt_ativ_prev_w) * 100;
	end if;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
