CREATE OR REPLACE FUNCTION bsc_obter_data_inf ( nr_seq_inf_p bigint) RETURNS timestamp AS $body$
DECLARE


cd_ano_w		smallint;
cd_periodo_w		smallint;
ie_periodo_w		varchar(1);
dt_periodo_w		timestamp;
nr_seq_indicador_w		bigint;


BEGIN


if (coalesce(nr_seq_inf_p,0) <> 0) then
	select	max(cd_ano),
		max(cd_periodo),
		max(nr_seq_indicador)
	into STRICT	cd_ano_w,
		cd_periodo_w,
		nr_seq_indicador_w
	from	bsc_ind_inf
	where	nr_sequencia = nr_seq_inf_p;
end if;

select	coalesce(ie_periodo,'M')
into STRICT	ie_periodo_w
from	bsc_indicador
where	nr_sequencia = nr_seq_indicador_w;

begin
	if (ie_periodo_w = 'A') then
		dt_periodo_w	:= to_date('01/01/' || to_char(cd_ano_w),'dd/mm/yyyy');
	elsif (ie_periodo_w = 'M') then
		dt_periodo_w	:= to_date('01/' || to_char(cd_periodo_w,'09') || '/' || to_char(cd_ano_w),'dd/mm/yyyy');
	elsif (ie_periodo_w = 'T') then
		dt_periodo_w	:= to_date('01/' || to_char(cd_periodo_w,'09') || '/' || to_char(cd_ano_w),'dd/mm/yyyy');
	elsif (ie_periodo_w = 'S') then
		dt_periodo_w	:= to_date('01/' || to_char(cd_periodo_w,'09') || '/' || to_char(cd_ano_w),'dd/mm/yyyy');
	elsif (ie_periodo_w = 'B') then
		dt_periodo_w	:= to_date('01/' || to_char(cd_periodo_w,'09') || '/' || to_char(cd_ano_w),'dd/mm/yyyy');
	elsif (ie_periodo_w = 'Q') then
		dt_periodo_w	:= to_date('01/' || to_char(cd_periodo_w,'09') || '/' || to_char(cd_ano_w),'dd/mm/yyyy');
	end if;

exception when others then
	dt_periodo_w	:= null;
end;

return	dt_periodo_w;

end;
