SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bsc_obter_encerradas_30_dias (dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE



pr_encerradas_w         double precision;


BEGIN


SELECT SUM(qt_encerradas_auto) / SUM(qt_total_encerradas) * 100 Percentual
into STRICT pr_encerradas_w
FROM (SELECT COUNT(*) qt_encerradas_auto,
                   0 qt_total_encerradas
        FROM    man_ordem_servico a,
                man_ordem_serv_tecnico b
        where   b.nr_seq_ordem_serv = a.nr_sequencia
        and     b.nr_seq_tipo  = 111
        AND     b.dt_historico BETWEEN TRUNC(dt_inicial_p,'mm') AND dt_final_p

UNION ALL

        SELECT 0 qt_encerradas_auto,
                   COUNT(*)  qt_total_encerradas
        FROM   man_ordem_servico a,
                   MAN_ORDEM_SERV_ESTAGIO b
        WHERE  a.nr_Sequencia = b.nr_Seq_ordem
        AND        IE_STATUS_ORDEM = 3
        AND        a.NR_SEQ_ESTAGIO  = 9
        AND        b.NR_SEQ_ESTAGIO  = 9
        AND        b.dt_atualizacao BETWEEN TRUNC(dt_inicial_p,'mm') AND dt_final_p) alias6;

return pr_encerradas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
