CREATE OR REPLACE FUNCTION bsc_obter_iniciativa_dt_max ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10) := '';
qt_hist_w		bigint;


BEGIN
select	count(*)
into STRICT	qt_hist_w
from	man_ordem_serv_tecnico
where	nr_seq_ordem_serv = nr_sequencia_p;

if (qt_hist_w > 0) then
	begin
	select	to_char(max(dt_atualizacao),'dd/mm/yyyy')
	into STRICT	ds_retorno_w
	from	man_ordem_serv_tecnico
	where	nr_seq_ordem_serv = nr_sequencia_p;
	end;
end if;

return	ds_retorno_w;

end;
