CREATE OR REPLACE FUNCTION bsc_obter_inovacoes (dt_inicial_p timestamp, dt_final_p timestamp, nr_seq_gerencia_p bigint default null) RETURNS bigint AS $body$
DECLARE



pr_inovacoes_w		double precision;


BEGIN

select	round(dividir((sum(qt_inov)*100),sum(qt_total)),2) qt_percent
into STRICT	pr_inovacoes_w
from (
	SELECT	round((dividir(sum(qt_minuto),60))::numeric,2) qt_inov,
			0 qt_total
	from (
		SELECT	sum(b.qt_minuto) qt_minuto
		from	gerencia_wheb g,
			grupo_desenvolvimento d,
			usuario_grupo_des e,
			man_ordem_servico a,
			man_ordem_serv_ativ b,
			usuario c
		where	a.nr_sequencia = b.nr_seq_ordem_serv
		and	c.nm_usuario   = b.nm_usuario_exec
		and 	g.nr_sequencia = d.nr_seq_gerencia
		and	d.nr_sequencia = e.nr_seq_grupo
		and 	e.nm_usuario_grupo = c.nm_usuario
		and 	g.ie_area_gerencia = 'DES'
		and (g.nr_sequencia = nr_seq_gerencia_p or coalesce(nr_seq_gerencia_p::text, '') = '')
		and	a.nr_seq_nivel_valor in (1,3)
		and 	b.dt_atividade between trunc(dt_inicial_p,'month') and dt_final_p
		and	not exists (
						select	1
						from	man_ordem_serv_tecnico x
						where	a.nr_sequencia = x.nr_seq_ordem_serv
						and	nr_seq_tipo = 129)
		and	a.nr_seq_classif <> 49) alias15
	
union all

	select	0 qt_inov,
			round((dividir(sum(qt_minuto),60))::numeric,2) qt_total
	from (
		select	sum(b.qt_minuto) qt_minuto
		from	gerencia_wheb g,
			grupo_desenvolvimento d,
			usuario_grupo_des e,
			man_ordem_servico a,
			man_ordem_serv_ativ b,
			usuario c
		where	a.nr_sequencia = b.nr_seq_ordem_serv
		and	c.nm_usuario   = b.nm_usuario_exec
		and 	g.nr_sequencia = d.nr_seq_gerencia
		and 	d.nr_sequencia = e.nr_seq_grupo
		and 	e.nm_usuario_grupo = c.nm_usuario
		and 	g.ie_area_gerencia = 'DES'
		and (g.nr_sequencia = nr_seq_gerencia_p or coalesce(nr_seq_gerencia_p::text, '') = '')
		and	(a.nr_seq_nivel_valor IS NOT NULL AND a.nr_seq_nivel_valor::text <> '')
		and 	b.dt_atividade between trunc(dt_inicial_p,'month') and dt_final_p
		and	not exists (
						select	1
						from	man_ordem_serv_tecnico x
						where	a.nr_sequencia = x.nr_seq_ordem_serv
						and	nr_seq_tipo = 129)
		and	a.nr_seq_classif <> 49) alias26) alias27;

return pr_inovacoes_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
