CREATE OR REPLACE FUNCTION bsc_obter_liberacoes_aval ( cd_perfil_p bigint, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, nr_seq_tipo_aval_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_permite_liberar_w	varchar(1);
ie_permite_registrar_w	varchar(1);
ie_permite_visualizar_w	varchar(1);
ie_permite_modificar_w	varchar(1);
qt_possui_registro_w 	bigint;

BEGIN
select 	count(*)
into STRICT	qt_possui_registro_w
from	med_perfil_avaliacao
where	nr_seq_tipo_aval = nr_seq_tipo_aval_p
and (cd_perfil = cd_perfil_p or coalesce(cd_perfil::text, '') = '')
and (cd_estabelecimento = cd_estabelecimento_p or coalesce(cd_estabelecimento::text, '') = '')
and (cd_setor_atendimento = cd_setor_atendimento_p or coalesce(cd_setor_atendimento::text, '') = '');


select 	max(ie_permite_liberar),
	max(ie_permite_registrar),
	max(ie_permite_visualizar),
	max(ie_permite_modificar)
into STRICT	ie_permite_liberar_w,
	ie_permite_registrar_w,
	ie_permite_visualizar_w,
	ie_permite_modificar_w
from	med_perfil_avaliacao
where	nr_seq_tipo_aval = nr_seq_tipo_aval_p
and (cd_perfil = cd_perfil_p or coalesce(cd_perfil::text, '') = '')
and (cd_estabelecimento = cd_estabelecimento_p or coalesce(cd_estabelecimento::text, '') = '')
and (cd_setor_atendimento = cd_setor_atendimento_p or coalesce(cd_setor_atendimento::text, '') = '');

if (ie_opcao_p = 'L') then
	ds_retorno_w := ie_permite_liberar_w;
end if;

if (ie_opcao_p = 'R') then
	ds_retorno_w := ie_permite_registrar_w;
end if;

if (ie_opcao_p = 'V') then
	ds_retorno_w := ie_permite_visualizar_w;
end if;

if (ie_opcao_p = 'M') then
	ds_retorno_w := ie_permite_modificar_w;
end if;

if (qt_possui_registro_w = 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
