CREATE OR REPLACE FUNCTION bsc_obter_os_encerrada_desenv ( dt_inicio_p timestamp, dt_fim_p timestamp, nr_seq_gerencia_p bigint default null) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	bigint;


BEGIN
select	count(*)
into STRICT	qt_retorno_w
from	man_ordem_servico a,
	grupo_desenvolvimento b,
	gerencia_wheb c
where	a.nr_seq_grupo_des	= b.nr_sequencia
and	b.nr_seq_gerencia	= c.nr_sequencia
and	c.ie_area_gerencia	= 'DES'
and (c.nr_sequencia = nr_seq_gerencia_p or coalesce(nr_seq_gerencia_p::text, '') = '')
and	a.dt_fim_real between trunc(dt_inicio_p) and fim_dia(dt_fim_p)
and	a.ie_status_ordem = '3'
and	exists (SELECT	1
		from	man_ordem_serv_estagio y,
			man_estagio_processo x
		where	x.nr_sequencia	= y.nr_seq_estagio
		and	y.nr_seq_ordem	= a.nr_sequencia
		and	x.ie_desenv	= 'S');

return	qt_retorno_w;

end;
