SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bsc_obter_perc_os_antiga ( dt_referencia_p timestamp, ie_area_p text, nr_seq_gerencia_p bigint default null) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	double precision	:= 0;
qt_total_os_w	bigint	:= 0;
dt_ref_mes_w	timestamp;
dt_fim_mes_w	timestamp;


BEGIN

dt_ref_mes_w	:= trunc(dt_referencia_p,'month');
dt_fim_mes_w	:= last_day(dt_ref_mes_w) + 86399/86400;

if (trunc(dt_referencia_p,'month') = trunc(clock_timestamp(),'month')) then

	if (ie_area_p in ('DES','PDES')) then
		select	COUNT(*)
		into STRICT	qt_total_os_w
		from	man_estagio_processo c,
			grupo_desenvolvimento b,
			gerencia_wheb g,
			man_ordem_servico a
		where	a.nr_seq_grupo_des = b.nr_sequencia
		and	c.nr_sequencia = a.nr_seq_estagio
		and	g.nr_sequencia = b.nr_seq_gerencia
		and	c.ie_desenv = 'S'
		and	a.ie_status_ordem <> '3'
		and	g.ie_area_gerencia = ie_area_p
		and (g.nr_sequencia = nr_seq_gerencia_p or coalesce(nr_seq_gerencia_p::text, '') = '');

		select	COUNT(*)
		into STRICT	qt_retorno_w
		from	man_estagio_processo c,
			grupo_desenvolvimento b,
			gerencia_wheb g,
			man_ordem_servico a
		where	a.nr_seq_grupo_des = b.nr_sequencia
		and	c.nr_sequencia = a.nr_seq_estagio
		and	g.nr_sequencia = b.nr_seq_gerencia
		and	c.ie_desenv = 'S'
		and	a.ie_status_ordem <> '3'
		and	g.ie_area_gerencia = ie_area_p
		and (g.nr_sequencia = nr_seq_gerencia_p or coalesce(nr_seq_gerencia_p::text, '') = '')
		and	a.dt_ordem_servico < clock_timestamp() - interval '30 days';

	elsif (ie_area_p = 'TEC') then

		select	COUNT(*)
		into STRICT	qt_total_os_w
		from	man_estagio_processo c,
			grupo_desenvolvimento b,
			gerencia_wheb g,
			man_ordem_servico a
		where	a.nr_seq_grupo_des = b.nr_sequencia
		and	c.nr_sequencia = a.nr_seq_estagio
		and	g.nr_sequencia = b.nr_seq_gerencia
		and	c.ie_tecnologia = 'S'
		and	a.ie_status_ordem <> '3'
		and 	exists (	SELECT *
				from usuario u
				where u.cd_pessoa_fisica = a.cd_pessoa_solicitante
				and u.cd_setor_atendimento <> 7)
		and	g.ie_area_gerencia = ie_area_p;

		select	COUNT(*)
		into STRICT	qt_retorno_w
		from	man_estagio_processo c,
			grupo_desenvolvimento b,
			gerencia_wheb g,
			man_ordem_servico a,
			man_localizacao l
		where	a.nr_seq_grupo_des = b.nr_sequencia
		and	c.nr_sequencia = a.nr_seq_estagio
		and	ie_terceiro = 'S'
		and	l.nr_sequencia = a.nr_seq_localizacao
		and	g.nr_sequencia = b.nr_seq_gerencia
		and	c.ie_tecnologia = 'S'
		and	a.ie_status_ordem <> '3'
		and 	exists (	SELECT *
				from usuario u
				where u.cd_pessoa_fisica = a.cd_pessoa_solicitante
				and u.cd_setor_atendimento <> 7)
		and	g.ie_area_gerencia = ie_area_p
		and	a.dt_ordem_servico < clock_timestamp() - interval '30 days';

	elsif (ie_area_p = 'IES') then

		select	COUNT(*)
		into STRICT	qt_total_os_w
		from	man_estagio_processo c,
			grupo_desenvolvimento b,
			gerencia_wheb g,
			man_ordem_servico a
		where	a.nr_seq_grupo_des = b.nr_sequencia
		and	c.nr_sequencia = a.nr_seq_estagio
		and	g.nr_sequencia = b.nr_seq_gerencia
		and	c.ie_infra = 'S'
		and	a.ie_status_ordem <> '3'
		and	g.ie_area_gerencia = ie_area_p;

		select	COUNT(*)
		into STRICT	qt_retorno_w
		from	man_estagio_processo c,
			grupo_desenvolvimento b,
			gerencia_wheb g,
			man_ordem_servico a
		where	a.nr_seq_grupo_des = b.nr_sequencia
		and	c.nr_sequencia = a.nr_seq_estagio
		and	g.nr_sequencia = b.nr_seq_gerencia
		and	c.ie_infra = 'S'
		and	a.ie_status_ordem <> '3'
		and	g.ie_area_gerencia = ie_area_p
		and	a.dt_ordem_servico < clock_timestamp() - interval '30 days';

	end if;	

	qt_retorno_w	:= dividir(qt_retorno_w * 100, qt_total_os_w);

else
	if (coalesce(nr_seq_gerencia_p::text, '') = '') then
		select	max(pr_os_antiga)
		into STRICT	qt_retorno_w
		from	w_indicador_desenv_apres
		where	dt_referencia = last_day(dt_ref_mes_w)
		and		ie_abrangencia = 'AREA'
		and		ie_area_gerencia = ie_area_p;
	else
		select	max(pr_os_antiga)
		into STRICT	qt_retorno_w
		from	w_indicador_desenv_apres
		where	dt_referencia = last_day(dt_ref_mes_w)
		and		ie_abrangencia = 'DES'
		and		ie_area_gerencia = ie_area_p
		and		nr_seq_gerencia = nr_seq_gerencia_p;
	end if;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION bsc_obter_perc_os_antiga ( dt_referencia_p timestamp, ie_area_p text, nr_seq_gerencia_p bigint default null) FROM PUBLIC;

