CREATE OR REPLACE FUNCTION bsc_obter_perc_satisfacao ( dt_referencia_p timestamp, ie_area_p text, nr_seq_gerencia_p bigint default null) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		double precision	:= 0;
qt_total_os_w		bigint	:= 0;
dt_ref_mes_w		timestamp;
dt_fim_mes_w		timestamp;


BEGIN

dt_ref_mes_w	:= trunc(dt_referencia_p,'month');
dt_fim_mes_w	:= last_day(dt_ref_mes_w) + 86399/86400;

if (ie_area_p = 'GERAL') then

	select	count(*)
	into STRICT	qt_total_os_w
	from	os_encerrada_satisf_gerencia_v
	where	dt_encerramento_ordem between dt_ref_mes_w and dt_fim_mes_w;

	select	count(*)
	into STRICT	qt_retorno_w
	from	os_insatisfacao_gerencia_v
	where	dt_encerramento_ordem between dt_ref_mes_w and dt_fim_mes_w;

else
	select	count(*)
	into STRICT	qt_total_os_w
	from	gerencia_wheb b,
		os_encerrada_satisf_gerencia_v a
	where	a.nr_seq_gerencia = b.nr_sequencia
	and	a.dt_encerramento_ordem between dt_ref_mes_w and dt_fim_mes_w
	and	b.ie_area_gerencia = ie_area_p
	and (b.nr_sequencia = nr_seq_gerencia_p or coalesce(nr_seq_gerencia_p::text, '') = '');

	select	count(*)
	into STRICT	qt_retorno_w
	from	gerencia_wheb b,
		os_insatisfacao_gerencia_v a
	where	a.nr_seq_gerencia = b.nr_sequencia
	and	a.dt_encerramento_ordem between dt_ref_mes_w and dt_fim_mes_w
	and	b.ie_area_gerencia = ie_area_p
	and (b.nr_sequencia = nr_seq_gerencia_p or coalesce(nr_seq_gerencia_p::text, '') = '');

end if;

qt_retorno_w	:= 100 - dividir(qt_retorno_w * 100, qt_total_os_w);

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
