CREATE OR REPLACE FUNCTION bsc_obter_se_inf_indic_periodo ( cd_ano_p bigint, cd_periodo_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_periodo_w		varchar(1) := 'N';
dt_inf_ind_bsc_w	timestamp;


BEGIN
if (cd_ano_p IS NOT NULL AND cd_ano_p::text <> '') and (cd_periodo_p IS NOT NULL AND cd_periodo_p::text <> '') and (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then
	begin
	dt_inf_ind_bsc_w := to_date('01/' || cd_periodo_p || '/' || cd_ano_p,'dd/mm/yyyy');

	if (dt_inf_ind_bsc_w >= dt_inicial_p) and (dt_inf_ind_bsc_w <= dt_final_p) then
		begin
		ie_periodo_w := 'S';
		end;
	end if;
	end;
end if;
return ie_periodo_w;
end;
