CREATE OR REPLACE FUNCTION bsc_obter_se_mes_fechado ( cd_empresa_p bigint, cd_estabelecimento_p bigint, cd_ano_p bigint, cd_periodo_p bigint) RETURNS varchar AS $body$
DECLARE



nr_seq_mes_ref_w			bigint;
dt_referencia_w			timestamp;
ie_fechado_w			varchar(1);


BEGIN

dt_referencia_w	:= to_date('01/' || to_char(cd_periodo_p,'09') || '/' || to_char(cd_ano_p),'dd/mm/yyyy');


select	max(nr_sequencia)
into STRICT	nr_seq_mes_ref_w
from	ctb_Mes_ref
where	cd_empresa	= cd_empresa_p
and	dt_referencia	= dt_referencia_w;

ie_fechado_w	:= substr(Ctb_Obter_Se_Mes_Fechado( nr_seq_mes_ref_w, cd_estabelecimento_p),1,1);

return ie_fechado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
