CREATE OR REPLACE FUNCTION bsc_obter_se_resp_indicador ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
cd_responsavel_w		varchar(10);
ds_retorno_w		varchar(1) := 'N';

c01 CURSOR FOR
SELECT	cd_pessoa_fisica
from	bsc_ind_resp
where	nr_seq_indicador = nr_sequencia_p
and	clock_timestamp() between dt_inicio_vigencia and fim_dia(dt_fim_vigencia);


BEGIN
select	cd_pessoa_fisica
into STRICT	cd_responsavel_w
from	usuario
where	nm_usuario = nm_usuario_p;

open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (cd_responsavel_w = cd_pessoa_fisica_w) then
		begin
		ds_retorno_w := 'S';
		end;
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
