CREATE OR REPLACE FUNCTION bsc_obter_se_resp_indic ( nr_sequencia_p bigint, dt_referencia_p timestamp, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_usuario_w		varchar(10);
dt_referencia_w			timestamp;
ie_responsavel_w		varchar(1) := 'N';
nr_seq_indicador_w		bigint;
cd_resp_indic_w			varchar(10);
qt_regra_w			bigint;


BEGIN 
 
dt_referencia_w		:= trunc(coalesce(dt_referencia_p,clock_timestamp()),'dd');
cd_pessoa_usuario_w	:= obter_pessoa_fisica_usuario(nm_usuario_p,'C');
cd_resp_indic_w		:= bsc_obter_resp_indic(nr_sequencia_p,dt_referencia_w,'C');
 
if (cd_resp_indic_w = cd_pessoa_usuario_w) then 
	ie_responsavel_w	:= 'S';
else 
	begin 
	select	count(*) 
	into STRICT	qt_regra_w 
	from	bsc_ind_resp 
	where	nr_seq_indicador	= nr_sequencia_p 
	and	cd_pessoa_fisica	= cd_pessoa_usuario_w 
	and	((dt_inicio_vigencia <= dt_referencia_w ) and (coalesce(dt_fim_vigencia,dt_referencia_w) >= dt_referencia_w));
 
	if (qt_regra_w > 0) then 
		ie_responsavel_w	:= 'S';
	else 
		begin 
		select	count(*) 
		into STRICT	qt_regra_w 
		from	bsc_ind_resp a, 
			bsc_indicador b 
		where	a.nr_seq_indicador	= b.nr_sequencia 
		and	b.nr_seq_superior	= nr_sequencia_p 
		and	a.cd_pessoa_fisica	= cd_pessoa_usuario_w 
		and	((a.dt_inicio_vigencia <= dt_referencia_w ) and (coalesce(a.dt_fim_vigencia,dt_referencia_w) >= dt_referencia_w));
		 
		if (qt_regra_w > 0) then 
			ie_responsavel_w	:= 'S';
		end if;
		end;
	end if;
	end;
end if;
 
return	ie_responsavel_w;
 
end;
$body$
