CREATE OR REPLACE FUNCTION bsc_obter_se_tipo_indicador ( nr_seq_indicador_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


qt_possui_indic_inferior_w	integer;
ds_retorno_w			varchar(1);


BEGIN
select	count(*)
into STRICT	qt_possui_indic_inferior_w
from	bsc_indicador
where	nr_seq_superior = nr_seq_indicador_p;

if (qt_possui_indic_inferior_w = 0) then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	bsc_indicador
	where	nr_sequencia = nr_seq_indicador_p
	and	ie_tipo = ie_tipo_p;
else
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	bsc_indicador
	where	nr_seq_superior = nr_seq_indicador_p
	and	ie_tipo = ie_tipo_p;

	if (ds_retorno_w = 'N') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	bsc_indicador
		where	nr_sequencia = nr_seq_indicador_p
		and	ie_tipo = ie_tipo_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
