CREATE OR REPLACE FUNCTION bsc_obter_valor_intervalo ( nr_seq_ind_inf_p bigint, nr_seq_formula_p bigint) RETURNS bigint AS $body$
DECLARE


cd_ano_w			smallint;
cd_periodo_w			smallint;
nr_seq_indicador_w			bigint;
qt_real_indicador_w			double precision;
qt_real_w				double precision;
vl_minimo_w			double precision;
vl_maximo_w			double precision;
vl_resultado_w			double precision;

c01 CURSOR FOR
SELECT	a.vl_minimo,
	a.vl_maximo,
	a.vl_resultado
from	bsc_regra_form_item a
where	a.nr_seq_formula	= nr_seq_formula_p
order by a.nr_Seq_calc;


BEGIN
/* Buscar o indicador de referencia para buscar se o valor de resultado deste está dentro do intervalo informado,
gerando o resultado par ao INDICADOR que está sendo CALCULADO */
select	nr_seq_ind_ref
into STRICT	nr_seq_indicador_w
from	bsc_regra_formula
where	nr_sequencia	= nr_seq_formula_p;

/* Ano/período que está sendo calculado */

select	cd_ano,
	cd_periodo
into STRICT	cd_ano_w,
	cd_periodo_w
from	bsc_ind_inf
where	nr_sequencia 	= nr_seq_ind_inf_p;

/*Quantidade Real no período do Indicador de Referencia que está sendo utilizado para comparar os valores*/

select	coalesce(qt_real,0)
into STRICT	qt_real_indicador_w
from	bsc_ind_inf
where	nr_seq_indicador	= nr_seq_indicador_w
and	cd_ano		= cd_ano_w
and	cd_periodo	= cd_periodo_w;

open C01;
loop
fetch C01 into
	vl_minimo_w,
	vl_maximo_w,
	vl_resultado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	/* Se o real do indicador estiver dentro do período, o VL_RESULTADO é o novo QT_REAL do indicador*/

	if (qt_real_indicador_w >= vl_minimo_w) and (qt_real_indicador_w <= vl_maximo_w) then
		qt_real_w	:= vl_resultado_w;
	end if;

	end;
end loop;
close C01;

return	qt_real_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION bsc_obter_valor_intervalo ( nr_seq_ind_inf_p bigint, nr_seq_formula_p bigint) FROM PUBLIC;

