SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION bsc_prod_sup_nivel_2 ( dt_inicial_p timestamp, dt_final_p timestamp ) RETURNS bigint AS $body$
DECLARE


result_w		double precision;
qt_colaborador_w	bigint;
qt_ordem_w		bigint;
qt_ordem_geral_w	bigint;
nm_usuario_w		varchar(100);

c01 CURSOR FOR
	SELECT	distinct nm_usuario_grupo
	from	gerencia_wheb_grupo_usu
	where	nr_seq_grupo = 230;


BEGIN
	qt_colaborador_w := 0;
	qt_ordem_geral_w := 0;
	result_w := 0;

	open c01;
	loop
	fetch c01 into
		nm_usuario_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			select	count(a.nr_sequencia)
			into STRICT	qt_ordem_w
			from	man_ordem_servico a
			where	(a.nr_seq_grupo_sup IS NOT NULL AND a.nr_seq_grupo_sup::text <> '')
			and	exists (SELECT	1
					from	man_ordem_serv_estagio y
					where	verifica_se_estagio_nv2(y.nr_seq_estagio) = 'S'
					and	y.nr_seq_ordem = a.nr_sequencia)
			and	exists (select	1
					from	man_ordem_serv_ativ w
					where	w.nr_seq_ordem_serv = a.nr_sequencia
					and (select	trunc(min(dt_atividade))
						from	man_ordem_serv_ativ
						where	lower(nm_usuario) = lower(nm_usuario_w)
						and	nr_seq_ordem_serv = a.nr_sequencia) between trunc(dt_inicial_p) and trunc(dt_final_p));

			qt_ordem_geral_w := (qt_ordem_geral_w + qt_ordem_w);
			qt_colaborador_w := (qt_colaborador_w + 1);
		end;
	end loop;
	close c01;

	result_w := (qt_ordem_geral_w / qt_colaborador_w);

	return	result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
