CREATE OR REPLACE FUNCTION buscar_action_evento ( nr_sequencia_p OBJ_SCHEMATIC_EVENTO.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	SELECT SUBSTR(OBTER_VALOR_DOMINIO(7697,X.IE_TIPO_EVENTO),1,255)
  into STRICT ds_retorno_w
  FROM	OBJ_SCHEMATIC_EVENTO X WHERE X.nr_sequencia = nr_sequencia_p;

end if;

return    ds_retorno_w;

end;
$body$
