SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION buscar_escala_atendimento ( nr_atendimento_p bigint , ds_escala_p text) RETURNS bigint AS $body$
DECLARE

    /*
        ds_escala_p: dominio 8892
    */
nr_result_w bigint;


BEGIN
    if (ds_escala_p = 'M') then -- Morse
        select  max(qt_pontuacao)
        into STRICT    nr_result_w
        from    escala_morse
        where   nr_atendimento = nr_atendimento_p
        and     nr_sequencia = (SELECT  max(nr_sequencia)
                                from    escala_morse
                                where   nr_atendimento = nr_atendimento_p);
    elsif (ds_escala_p = 'S') then -- Stratify
        select  max(qt_score)
        into STRICT    nr_result_w
        from    escala_stratify
        where   nr_atendimento = nr_atendimento_p
        and     nr_sequencia = (SELECT  max(nr_sequencia)
                                from    escala_stratify
                                where   nr_atendimento = nr_atendimento_p);
    elsif (ds_escala_p = 'E') then -- EARQ
        select  max(qt_pontuacao)
        into STRICT    nr_result_w
        from    escala_earq
        where   nr_atendimento = nr_atendimento_p
        and     nr_sequencia = (SELECT  max(nr_sequencia)
                                from    escala_earq
                                where   nr_atendimento = nr_atendimento_p);
    elsif (ds_escala_p = 'J') then -- JH-FRAT
        select  max(qt_pontuacao)
        into STRICT    nr_result_w
        from    escala_frat
        where   nr_atendimento = nr_atendimento_p
        and     nr_sequencia = (SELECT  max(nr_sequencia)
                                from    escala_frat
                                where   nr_atendimento = nr_atendimento_p);
    elsif (ds_escala_p = 'F') then -- Score II - EIF II
        select max(qt_pontos)
        into STRICT nr_result_w
        from escala_eif_ii
        where nr_atendimento = nr_atendimento_p
        and nr_sequencia = (SELECT max(nr_sequencia)
                            from escala_eif_ii
                            where nr_atendimento = nr_atendimento_p);
    end if;

    return nr_result_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
