CREATE OR REPLACE FUNCTION buscar_etapa_sol_agrupada (nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_cpoe_p prescr_material.nr_seq_mat_cpoe%type, nr_etapa_p prescr_mat_hor.nr_etapa_sol%type) RETURNS bigint AS $body$
DECLARE


nr_etapa_sol_agrupada_w		bigint;


BEGIN

select  max((a.nr_etapa_sol + ( select count(1)
				from    prescr_mat_hor c,
					prescr_material d
				where   d.nr_prescricao < a.nr_prescricao
				and     d.nr_seq_mat_cpoe = b.nr_seq_mat_cpoe
				and     c.nr_prescricao = d.nr_prescricao
				and     c.nr_seq_solucao = d.nr_sequencia_solucao
				and     (c.dt_lib_horario IS NOT NULL AND c.dt_lib_horario::text <> '')
				and     c.ie_horario_especial = 'N'))) etapa_sol_agrupada
into STRICT    nr_etapa_sol_agrupada_w
from    prescr_mat_hor a,
        prescr_material b
where   b.nr_seq_mat_cpoe = nr_seq_cpoe_p
and     a.nr_prescricao = nr_prescricao_p 
and     a.nr_etapa_sol = nr_etapa_p
and     a.nr_prescricao = b.nr_prescricao
and     a.nr_seq_solucao = b.nr_sequencia_solucao;

return nr_etapa_sol_agrupada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
